import React from 'react'
import Highcharts from 'highcharts';
import crossfilter from 'crossfilter';

function PerformanceGraph() {

    async function getData(url) {
        const response = await fetch(url, {
            method: 'GET'
        });
        return response.json(); 
    }

    const prepareData = (data) => {
        let cf = crossfilter(data);
        let analystDim = cf.dimension((d) => {return d.analyst})
        let series = analystDim.group().reduceSum((d) => {return d.amount})
        console.log(series)
        console.log(cf)
    }

    const loadChart = () => {
        var URL = 'http://localhost/API/Admin/fetch-task-performance.php?status=';
        let filter = document.getElementsByName('status2');
        for(let i=0; i<filter.length; i++) {
            if(filter[i].checked) {
                URL += filter[i].value;
                break;
            }
        }
        console.log(URL)

        getData(URL)
        .then(data => {
            console.log(data)
            data = prepareData(data.data)
            renderChart(data)
        });
    }


    const renderChart = (data) => {
        Highcharts.chart('graph-2', {

            title: {
                text: 'Solar Employment Growth by Sector, 2010-2016'
            },

            subtitle: {
                text: 'Source: thesolarfoundation.com'
            },

            yAxis: {
                title: {
                    text: 'Number of Employees'
                }
            },

            xAxis: {
                accessibility: {
                    rangeDescription: 'Range: 2010 to 2017'
                }
            },

            legend: {
                layout: 'vertical',
                align: 'right',
                verticalAlign: 'middle'
            },

            plotOptions: {
                series: {
                    label: {
                        connectorAllowed: false
                    },
                    pointStart: 2010
                }
            },

            series: [{
                name: 'Installation',
                data: [43934, 52503, 57177, 69658, 97031, 119931, 137133, 154175]
            }, {
                name: 'Manufacturing',
                data: [24916, 24064, 29742, 29851, 32490, 30282, 38121, 40434]
            }, {
                name: 'Sales & Distribution',
                data: [11744, 17722, 16005, 19771, 20185, 24377, 32147, 39387]
            }, {
                name: 'Project Development',
                data: [null, null, 7988, 12169, 15112, 22452, 34400, 34227]
            }, {
                name: 'Other',
                data: [12908, 5948, 8105, 11248, 8989, 11816, 18274, 18111]
            }],

            responsive: {
                rules: [{
                    condition: {
                        maxWidth: 500
                    },
                    chartOptions: {
                        legend: {
                            layout: 'horizontal',
                            align: 'center',
                            verticalAlign: 'bottom'
                        }
                    }
                }]
            }

        });
    }
    return (
        <>
            <div id="graph-2">
            </div>
            <div className='input-block'>
                <div className='customized-radio-black'>
                    <label>
                        <input type="radio" value="Completed" name="status2" checked/>
                        <span className=''>Completed</span>
                    </label>
                    <label>
                        <input type="radio" value="Pending" name="status2" />
                        <span>Pending</span>
                    </label>
                    <label>
                        <input type="radio" value="*" name="status2" />
                        <span>Accumulated</span>
                    </label>
                </div> 
            </div>
            <div className='flex-row jc-e'>
                <button className='btn btn-primary btn-medium' onClick={loadChart}><i className='fas fa-sync'/> LoadChart</button>
            </div>
        </>
    )
}

export default PerformanceGraph
