import React, { useState } from 'react'
import Highcharts from 'highcharts';
import crossfilter from 'crossfilter';
import Loading from '../utility/Loading';
import CustomRadio from '../utility/CustomRadio';

function PerformanceGraph() {

    var chartData = [];

    async function getData(url) {
        const response = await fetch(url, {
            method: 'GET'
        });
        return response.json(); 
    }

    function reduceAdd(p, v, nf) {
        p.data.push(v.amount);
        return p;
    }

    function reduceRemove(p, v, nf) {
        p.data.pop();
        return p;
    }

    function reduceInitial() {
        return {data: []};
    }

    const prepareData = (data, analystFilter = '') => {
        let cf = crossfilter(data);

        if(analystFilter) {
            cf.dimension((d) => {return d.analyst}).filter(analystFilter);
        }

        let analystDim = cf.dimension((d) => {return d.analyst});

        let series = analystDim.group().reduce(reduceAdd, reduceRemove, reduceInitial).all();

        let seriesData = [];
        for(let i=0; i<series.length; i++) {
            let obj = {
                name: series[i].key,
                data: series[i].value.data
            }
            seriesData.push(obj);
        }

        return seriesData
    }

    const loadChart = () => {
        var URL = 'http://localhost/API/Admin/fetch-task-performance.php';

        getData(URL)
        .then(data => {
            chartData = data.data;
            data = prepareData(data.data);
            let analystSelect = document.getElementById('analyst-filter-select');
            analystSelect.innerHTML = '';

            let option = document.createElement('option');
            option.innerText = 'Select Analyst Filter';
            option.value = '';
            option.selected = true;
            analystSelect.appendChild(option);

            for(let i=0; i<data.length; i++) {
                let option = document.createElement('option');
                option.innerText = data[i].name;
                option.value = data[i].name;
                analystSelect.appendChild(option);
            }
            renderChart(data);

        });
    }


    const updateChart = () => {
        let data = prepareData(chartData, document.getElementById('analyst-filter-select').value);
        renderChart(data);
    }

    const renderChart = (data) => {
        Highcharts.chart('graph-2', {

            title: {
                text: 'Analyst Performance'
            },

            subtitle: {
                text: null
            },
            credits: {
                enabled: false
            },

            yAxis: {
                title: {
                    text: 'Payment of task'
                }
            },

            xAxis: {
                title: {
                    text: 'Number of tasks'
                },
                accessibility: {
                    rangeDescription: 'Range: 2010 to 2017'
                }
            },

            legend: {
                layout: 'vertical',
                align: 'right',
                verticalAlign: 'middle'
            },

            plotOptions: {
                series: {
                    label: {
                        connectorAllowed: false
                    },
                    pointStart: 1
                }
            },

            series: data,

            responsive: {
                rules: [{
                    condition: {
                        maxWidth: 500
                    },
                    chartOptions: {
                        legend: {
                            layout: 'horizontal',
                            align: 'center',
                            verticalAlign: 'bottom'
                        }
                    }
                }]
            }

        });
    }
    return (
        <>
            <div id="graph-2">
                <Loading />
            </div>
            <div className='input-block'>
                <div className='customized-radio-black'>
                    <CustomRadio 
                        value='Completed' 
                        text='Completed'
                        name='status2'
                        icon='fas fa-check'
                    />
                    <CustomRadio 
                        value='Pending' 
                        text='Pending'
                        name='status2'
                        icon='fas fa-box-open'
                    />
                    <CustomRadio 
                        value='*' 
                        text='Accumulated'
                        name='status2'
                        icon='fas fa-box'
                        checked={true}
                    />
                </div> 
            </div>
            <div className='flex-row jc-sb'>
                <select id="analyst-filter-select" onChange={updateChart}>
                    <option disabled>Select Analyst</option>
                </select>
                <button className='btn btn-primary btn-medium' onClick={loadChart}><i className='fas fa-sync'/> Refresh</button>
            </div>
            {loadChart()}
        </>
    )
}

export default PerformanceGraph
