import React from 'react'
import {Link} from 'react-router-dom'
import '../components/css/Signup.css'

function Signup() {

    async function postData(url = '', data = {}) {
        const response = await fetch(url, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(data)
        });
        return response.text(); 
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        let inputBlock = e.target.getElementsByClassName('input-block');

        let inputs1 = inputBlock[0].getElementsByTagName('input');
        let inputs2 = inputBlock[1].getElementsByTagName('input');
        let inputs3 = inputBlock[2].getElementsByTagName('input');

        let reqData = {
            firstName: inputs1[0].value,
            lastName: inputs1[1].value,
            email: inputs2[0].value,
            contact: inputs2[1].value,
            password: inputs3[0].value,
            confirmPassword: inputs3[1].value
        }
        console.log(reqData);
        postData('http://localhost/API/Admin/register.php', reqData)
          .then(data => {
            console.log(data);
          });
    }



    return (
        <div className='signup-container flex-row jc-c ai-c'>
            <form id="signup-form" onSubmit={handleSubmit}>
                <div className='header'>Admin Registration</div>
                <div className='input-block'>
                    <div className='input-container-floating'>
                        <label>First Name</label>
                        <input type="text" name="firstName" />
                    </div>
                    <div className='input-container-floating'>
                        <label>Last Name</label>
                        <input type="text" name="lastName" />
                    </div>
                </div>
                <div className='input-block'>
                    <div className='input-container-floating'>
                        <label>E-mail</label>
                        <input type="text" name="email" />
                    </div>
                    <div className='input-container-floating'>
                        <label>Contact</label>
                        <input type="text" name="contact" />
                    </div>
                </div>
                <div className='input-block'>
                    <div className='input-container-floating'>
                        <label>Password</label>
                        <input type="password" name="password" />
                    </div>
                    <div className='input-container-floating'>
                        <label>Confirm Password</label>
                        <input type="password" name="confirmPassword" />
                    </div>
                </div>
                <div className='flex-row jc-sb'>
                    <div className='btn btn-secondary btn-medium'>Reset</div>
                    <button className='btn btn-primary btn-medium'>Register</button>
                </div>
                <div className='footer'>
                    Already have an account? <Link to='/signin'>Signin</Link>
                </div>
            </form>
        </div>
    )
}

export default Signup
