import React from 'react'
import Highcharts from 'highcharts';
function TaskGraph() {


    async function postData(url) {
        const response = await fetch(url, {
            method: 'GET'
          });
        return response.json(); 
    }

    const loadChart = () => {
        const URL = 'http://localhost/API/Admin/fetch-task-share.php?status=';
        let filter = document.getElementsByName('status');
        URL = filter[0].checked ? URL + 'Completed' : URL + 'Pending';

        getData(URL)
        .then(data => {
            loadChart([data.data])
        });
    }


    const renderChart = (data) => {
        // Radialize the colors
        Highcharts.setOptions({
            colors: Highcharts.map(Highcharts.getOptions().colors, function (color) {
                return {
                    radialGradient: {
                        cx: 0.5,
                        cy: 0.3,
                        r: 0.7
                    },
                    stops: [
                        [0, color],
                        [1, Highcharts.color(color).brighten(-0.3).get('rgb')] // darken
                    ]
                };
            })
        });

        // Build the chart
        Highcharts.chart('graph-1', {
            chart: {
                plotBackgroundColor: null,
                plotBorderWidth: null,
                plotShadow: false,
                type: 'pie'
            },
            title: {
                text: 'Analyst task completion share'
            },
            tooltip: {
                pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'
            },
            accessibility: {
                point: {
                    valueSuffix: '%'
                }
            },
            plotOptions: {
                pie: {
                    allowPointSelect: true,
                    cursor: 'pointer',
                    dataLabels: {
                        enabled: true,
                        format: '<b>{point.name}</b>: {point.percentage:.1f} %',
                        connectorColor: 'silver'
                    }
                }
            },
            credits: {
                enabled: false
            },
            series: data
        });
    }
    return (
        <>
            <div id="graph-1">
            </div>
            <div className='input-block'>
                <div className='customized-radio-black'>
                    <label>
                        <input type="radio" value="Completed" name="status" checked/>
                        <span className=''>Completed</span>
                    </label>
                    <label>
                        <input type="radio" value="Pending" name="status" />
                        <span>Pending</span>
                    </label>
                </div>
            </div>
            <div className='flex-row jc-e'>
                <button className='btn btn-primary btn-medium' onClick={loadChart}>LoadChart</button>
            </div>
        </>
    )
}

export default TaskGraph
